INSERT INTO `top_university_school_detail_info`(`id`, `school_id`, `school_no`, `school_name`, `school_logo`, `school_address`, `school_belong`, `school_create_time`, `school_area`, `school_library_num`, `school_academician_num`, `school_doctor_num`, `school_master_num`, `school_lab_num`, `school_subject_num`, `school_phone`, `school_postcode`, `school_email`, `school_content`, `school_province_name`, `school_province_id`, `school_city_name`, `school_city_id`, `school_town_name`, `school_level_name`, `school_level_id`, `school_type_name`, `school_type`, `school_nature_name`, `school_zsb_site`, `school_site`, `school_211`, `school_985`, `school_short`) VALUES (2779, '304', '442003200', '广州美术学院', 'https://static-data.eol.cn/upload/logo/304.jpg', '校本部： 广州市番禺区大学城外环西路168号,昌岗校区：广州市海珠区昌岗东路257号', '广东省', '1953', '564.00', '0', '0', '0', '6', '0', '0', '020-84017740', '510006', 'zsb@gzarts.edu.cn', '五岭以南，南海之滨的广东，是中国的南大门，自秦汉始即通过海洋与世界交往。西晋伊始，广州成为海上丝绸之路的主港，唐宋时为中国第一大港，明清时则是唯一的对外贸易港口。它有得天独厚的地理区位优势及深厚的文化历史积淀。得风气之先，领时代之新，走变革之路的广东是中国近代大变革进程的先行者。胸怀开阔，兼容并蓄，尚新求变，是广东人也是岭南文化的', '广东', '44', '广州市', '4401', '', '普通本科', '2001', '艺术类', '5009', '公办', 'http://zs.gzarts.edu.cn/', 'http://www.gzarts.edu.cn', '2', '2', '广美');
